mapper.delete:
delete from t_document

 id=#{id}
      and state=#{ state }
      and author=#{ author }
      and content=#{ content }
      and title=#{ title }

mapper.update:
update t_document

      id=#{ id },
      state=#{ state },
      author=#{ author },
      content=#{ content },
      title=#{ title },


 id=#{id}
      and state=#{ state }
      and author=#{ author }
      and content=#{ content }
      and title=#{ title }

mapper.select:
select id,state,author,content,title,create_time from t_document

      and id=#{ id }
      and state=#{ state }
      and author=#{ author }
      and content=#{ content }
      and title=#{ title }

select id,state,author,content,title,create_time from t_document

      and id=#{ id }
      and state=#{ state }
      and author=#{ author }
      and content=#{ content }
      and title=#{ title }

limit #{offset},#{rows}

select id,state,author,content,title,create_time from t_document

      and id=#{ id }
      and state=#{ state }
      and author=#{ author }
      and content=#{ content }
      and title=#{ title }

limit 0,1

select count(1) from t_document

      and id = #{ id }
      and state = #{ state }
      and author = #{ author }
      and content = #{ content }
      and title = #{ title }
