mapper.delete:
delete from t_review

 id=#{id}
      and time=#{ time }
      and remark=#{ remark }
      and doc_id=#{ doc_id }
      and dept_id=#{ dept_id }
      and state=#{ state }

mapper.update:
update t_review
id=#{ id }, time=#{ time }, remark=#{ remark }, doc_id=#{ doc_id }, dept_id=#{ dept_id }, state=#{ state },
id=#{id}

mapper.select:
   select 
	   t_review.id,
	   t_review.time,
	   t_review.remark,
	   doc_id,
	   dept_id,
	   t_review.state, 
	   tp.name as dept_name
   from t_review
   left join t_department tp on tp.id=t_review.dept_id

         and id=#{ id }
         and time=#{ time }
         and remark=#{ remark }
         and doc_id=#{ doc_id }
         and dept_id=#{ dept_id }
         and state=#{ state }

select id,time,remark,doc_id,dept_id,state from t_review

      and id=#{ id }
      and time=#{ time }
      and remark=#{ remark }
      and doc_id=#{ doc_id }
      and dept_id=#{ dept_id }
      and state=#{ state }

limit #{offset},#{rows}

select id,time,remark,doc_id,dept_id,state from t_review

      and id=#{ id }
      and time=#{ time }
      and remark=#{ remark }
      and doc_id=#{ doc_id }
      and dept_id=#{ dept_id }
      and state=#{ state }

limit 0,1

select count(1) from t_review

      and id = #{ id }
      and time = #{ time }
      and remark = #{ remark }
      and doc_id = #{ doc_id }
      and dept_id = #{ dept_id }
      and state = #{ state }
