mapper.delete:
delete from t_urge

 id=#{id}
      and userid=#{ userid }
      and target_id=#{ target_id }
      and content=#{ content }
      and deadline=#{ deadline }

mapper.update:
update t_urge
id=#{ id }, userid=#{ userid }, target_id=#{ target_id }, content=#{ content }, deadline=#{ deadline },
id=#{id}

mapper.select:
select id,userid,target_id,content,deadline from t_urge

      and id=#{ id }
      and userid=#{ userid }
      and target_id=#{ target_id }
      and content=#{ content }
      and deadline=#{ deadline }

select id,userid,target_id,content,deadline from t_urge

      and id=#{ id }
      and userid=#{ userid }
      and target_id=#{ target_id }
      and content=#{ content }
      and deadline=#{ deadline }

limit #{offset},#{rows}

select id,userid,target_id,content,deadline from t_urge

      and id=#{ id }
      and userid=#{ userid }
      and target_id=#{ target_id }
      and content=#{ content }
      and deadline=#{ deadline }

limit 0,1

select count(1) from t_urge

      and id = #{ id }
      and userid = #{ userid }
      and target_id = #{ target_id }
      and content = #{ content }
      and deadline = #{ deadline }
