mapper.delete:
delete from t_user

 id=#{id}
      and loginname=#{ loginname }
      and password=#{ password }
      and name=#{ name }

mapper.update:
update t_user
id=#{ id }, loginname=#{ loginname }, password=#{ password }, name=#{ name },
id=#{id}

mapper.select:
select * from t_user

      and id=#{ id }
      and loginname=#{ loginname }
      and password=#{ password }
      and name=#{ name }

select * from t_user

      and id=#{ id }
      and loginname=#{ loginname }
      and password=#{ password }
      and name=#{ name }

limit #{offset},#{rows}

select * from t_user

      and id=#{ id }
      and loginname=#{ loginname }
      and password=#{ password }
      and name=#{ name }

limit 0,1

select count(1) from t_user

      and id = #{ id }
      and loginname = #{ loginname }
      and password = #{ password }
      and name = #{ name }
